    pub fn stream(
        &self,
        input: S,
        config: Config,
        mode: StreamMode,
    ) -> impl Stream<Item = StreamEvent> {
        // Stream execution events
        stream! {
            for step in self.execute(input, config) {
                match mode {
                    StreamMode::Values => yield step.state,
                    StreamMode::Updates => yield step.updates,
                    StreamMode::Messages => yield step.messages,
                }
            }
        }
    }
}

